$(function(){
	 
    /*---------------------- Main menu With drop down -----------------------*/
		
		function menuSettings(){
			
			// detect drop down for adding class
			$(".main-menu ul li").find("> ul").parent().addClass("dropdown");
			
			// create mobile menu
			var menuHtml = $(".main-menu").html();
			$(".mobile-menu .container").html(menuHtml);
			
			// top links add to main nav (for mobile)
			var topnavHtml = $(".cart-login > ul").html();
			$(".mobile-menu ul.menu-list > li:last-child()").after(topnavHtml);
			
			// mobile menu toggle
			$(".mobile-menu .menu-toggle").click(function(){
				$(this).parent().find(">ul.menu-list").stop(true, true).slideToggle(150);
				$(this).toggleClass("active");
			});
			
		}
		
		menuSettings();
		
		// ----------------------------- Dropdown menu -----------------------------
		
		$(".main-menu > ul > li").hover(function(){
			$(this).find("> a").addClass("hoverClass");
			$(this).find(">ul").stop(true, false).slideToggle(200);     
			}, function(){
			$(this).find("> a").removeClass("hoverClass");
			$(this).find(">ul").stop(true, false).slideToggle(200);   
		});
		
		
		function menuMobile(){
			// drp menu 
			var allAccordion = $('.mobile-menu ul.menu-list > li > ul');
			var allAccordionItem = $('.mobile-menu ul.menu-list > li.dropdown > a');
			$(allAccordionItem).click(function() {
				
				if($(this).hasClass('open'))
				{
					$(this).removeClass('open');
					$(this).next().slideUp("slow");
				}
				else
				{
					allAccordion.slideUp("slow");
					allAccordionItem.removeClass('open');
					$(this).addClass('open');
					$(this).next().slideDown("slow");
					return false;
				}
			});
		}
		menuMobile();
        
    
        
        //*------------------ Sub-banners -----------------*/
        function makeBg(){
            $(".set-as-bg").each(function(){
                var imgurl = $(this).find("> span.make-bg img").attr('src');
                $(this).css( 'background-image', 'url(' + imgurl+ ')');
            });
         }
         makeBg();
    
        
        // owlCarousel home page top section wall product slider
        $(".how-work-slider").owlCarousel
        ({
           items:1,
           center: false,
           nav:true,
           dots:true,
           margin:15,
           loop:false,
           autoplay:false,
           navText:false,

           responsive:{
               0:{
                    items:1
                },
                320:{
                    items:1
                },
                480:{
                    items:1
                },
                600:{
                    items:2,
                    margin:15
                },
                767:{
                    items:2,
                    margin:15
                },
                991:{
                    items:3,
                    margin:15
                },
                1200:{
                    items:4,
					margin:10,
					// stagePadding: 100
                }
           }

         });
    
        $(".premium-prev").click(function(){
			$(".owl-prev").trigger('click');	 
		 });
		 $(".premium-next").click(function(){
			$(".owl-next").trigger('click');	 
		 });
    
    
        
    
        /*============================================================
          04-05-2019 New Pages (Arin,Ripe,Faq,Contact)
          ============================================================*/
        
        $('.faq-page-left ul li a').click(function() {
			if (location.pathname.replace(/^\//,'') == this.pathname.replace(/^\//,'') && location.hostname == this.hostname) {
				var target = $(this.hash);
				target = target.length ? target : $('[name=' + this.hash.slice(1) +']');
				if (target.length) {
					$('html,body').animate({
					scrollTop: target.offset().top -30
				}, 600);
				
				return false;
				}
			}
		});  
    
    
        /*---------------------- Tos-Left-Block Fixed -----------------------*/
		
		$("#test").scrollToFixed({
            marginTop: 5,
            limit: function() {
                var limit = 0;
                limit = $('.form-block').offset().top - $(this).outerHeight(true) - 5;
                return limit;
            }
        });
        


		/* ================================================================
			05-10-2021
			================================================================ */

		//Simple accordien
		var allAccordions = $('.accordion div.data');
		var allAccordionItems = $('.accordion .accordion-item');
		$('.accordion > .accordion-item').click(function() {
			if($(this).hasClass('open'))
			{
				$(this).removeClass('open');
				$(this).next().slideUp(150);
			}
			else
			{
				allAccordions.slideUp(150);
				allAccordionItems.removeClass('open');
				$(this).addClass('open');
				$(this).next().slideDown(150);
				return false;
			}
		});
    
    
        
        // Match Height 24-03-2020**************************************************
        $(".same").matchHeight({
            property: 'height',	
        });
        $(".cm-height").matchHeight({
            property: 'height',	
        });
        $(".equal-height").matchHeight({
            property: 'height',	
        });

		$('select').niceSelect();

});// main function


